import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS for styling

// Static userId for now (could be dynamic in real-world scenarios)
const userId = '66df023961c74851a7c06b5b';

const App = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState(''); // New field for due date
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null); // For editing a task
  const [notification, setNotification] = useState(''); // For success/error notifications
  const [filter, setFilter] = useState('all'); // Filter for task status
  const [searchTerm, setSearchTerm] = useState(''); // Search term for tasks

  // Fetch tasks when the component loads
  useEffect(() => {
    fetchTasks();
  }, []);

  // Function to fetch tasks from the API
  const fetchTasks = async () => {
    try {
      const response = await axios.get(`http://localhost:9000/api/tasks?userId=${userId}`);
      setTasks(response.data); // Assuming response.data contains an array of tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setNotification('Error fetching tasks.');
    }
  };

  // Function to handle task submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !dueDate) {
      alert('Please fill out all fields');
      return;
    }

    try {
      if (taskToEdit) {
        // Update an existing task
        const response = await axios.put(`http://localhost:9000/api/tasks/${taskToEdit._id}`, {
          title,
          description,
          dueDate, // Include due date in the update
          userId,
        });
        setTasks(tasks.map(task => task._id === response.data._id ? response.data : task));
        setTaskToEdit(null); // Reset edit state
        setNotification('Task updated successfully.');
      } else {
        // Add a new task
        const response = await axios.post('http://localhost:9000/api/tasks', {
          title,
          description,
          dueDate, // Include due date when creating a task
          userId,
        });
        setTasks([...tasks, response.data]);
        setNotification('Task added successfully.');
      }

      // Clear the form fields
      setTitle('');
      setDescription('');
      setDueDate(''); // Clear due date
    } catch (error) {
      console.error('Error adding/updating task:', error);
      setNotification('Error adding/updating task.');
    }
  };

  // Function to delete a task with confirmation
  const handleDelete = async (taskId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) {
      return; // Exit if user cancels the confirmation
    }

    try {
      await axios.delete(`http://localhost:9000/api/tasks/${taskId}`);
      setTasks(tasks.filter(task => task._id !== taskId));
      setNotification('Task deleted successfully.');
    } catch (error) {
      console.error('Error deleting task:', error);
      setNotification('Error deleting task.');
    }
  };

  // Function to toggle task status (completed/in-progress)
  const toggleTaskStatus = async (task) => {
    try {
      const updatedStatus = task.status === 'in-progress' ? 'completed' : 'in-progress';
      const response = await axios.put(`http://localhost:9000/api/tasks/${task._id}/status`, { status: updatedStatus });
      setTasks(tasks.map(t => t._id === task._id ? { ...t, status: updatedStatus } : t));
      setNotification(`Task marked as ${updatedStatus}.`);
    } catch (error) {
      console.error('Error toggling task status:', error);
      setNotification('Error updating task status.');
    }
  };

  // Function to populate task form with data for editing
  const handleEdit = (task) => {
    setTitle(task.title);
    setDescription(task.description);
    setDueDate(task.dueDate); // Populate due date for editing
    setTaskToEdit(task);
  };

  // Filter tasks by status
  const filteredTasks = tasks.filter(task => {
    if (filter === 'all') return true;
    return task.status === filter;
  });

  // Search tasks by title (use safe check to avoid toLowerCase() on undefined titles)
  const searchedTasks = filteredTasks.filter(task =>
    task.title?.toLowerCase().includes(searchTerm.toLowerCase()) // Safe check to avoid error
  );

  // Function to format date
  const formatDate = (date) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(date).toLocaleDateString(undefined, options);
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>

      {/* Notification */}
      {notification && <div className="notification">{notification}</div>}

      {/* Task Form */}
      <form onSubmit={handleSubmit}>
        <h2>{taskToEdit ? 'Edit Task' : 'Add Task'}</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="date"
          placeholder="Due Date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <button type="submit">{taskToEdit ? 'Update Task' : 'Add Task'}</button>
      </form>

      {/* Task Filters */}
      <div className="task-filters">
        <input
          type="text"
          placeholder="Search by title"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
      </div>

      {/* Task List */}
      <h2>Tasks</h2>
      {searchedTasks.length > 0 ? (
        <ul className="task-list">
          {searchedTasks.map((task) => (
            <li key={task._id} className="task-item">
              <strong>{task.title}</strong> - {task.description} - {task.status} - Due: {task.dueDate ? formatDate(task.dueDate) : 'N/A'}
              <div className="task-actions">
                <button onClick={() => toggleTaskStatus(task)}>
                  Mark as {task.status === 'in-progress' ? 'Completed' : 'In Progress'}
                </button>
                <button onClick={() => handleEdit(task)}>Edit</button>
                <button onClick={() => handleDelete(task._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default App;
