import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; 

const App = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [notification, setNotification] = useState('');
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [filterUser, setFilterUser] = useState('');

  useEffect(() => {
    fetchTasks();
    fetchUsers();
  }, [filterUser]);

  const fetchTasks = async () => {
    try {
      let url = 'http://localhost:9000/api/tasks'; 
      if (filterUser) {
        url += `?userId=${filterUser}`;
      }
      const response = await axios.get(url);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setNotification('Error fetching tasks.');
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:9000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      setNotification('Error fetching users.');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !dueDate || !selectedUser) {
      alert('Please fill out all fields');
      return;
    }

    try {
      if (taskToEdit) {
        const response = await axios.put(`http://localhost:9000/api/tasks/${taskToEdit._id}`, {
          title,
          description,
          dueDate,
          userId: selectedUser,
        });
        setTasks(tasks.map(task => task._id === response.data._id ? response.data : task));
        setTaskToEdit(null);
        setNotification('Task updated successfully.');
      } else {
        const response = await axios.post('http://localhost:9000/api/tasks', {
          title,
          description,
          dueDate,
          userId: selectedUser,
        });
        setTasks([...tasks, response.data]);
        setNotification('Task added successfully.');
      }

      setTitle('');
      setDescription('');
      setDueDate('');
      setSelectedUser('');
    } catch (error) {
      console.error('Error adding/updating task:', error);
      setNotification('Error adding/updating task.');
    }
  };

  const toggleTaskStatus = async (task) => {
    try {
      const newStatus = task.status === 'in-progress' ? 'completed' : 'in-progress';
      const response = await axios.put(`http://localhost:9000/api/tasks/${task._id}/status`, {
        status: newStatus,
      });
      setTasks(tasks.map(t => t._id === task._id ? { ...t, status: newStatus } : t));
      setNotification('Task status updated successfully.');
    } catch (error) {
      console.error('Error updating task status:', error);
      setNotification('Error updating task status.');
    }
  };

  const handleEdit = (task) => {
    setTaskToEdit(task);  
    setTitle(task.title);
    setDescription(task.description);
    setDueDate(task.dueDate);
    setSelectedUser(task.user);  
  };

  const handleDelete = async (taskId) => {
    try {
      await axios.delete(`http://localhost:9000/api/tasks/${taskId}`);
      setTasks(tasks.filter(task => task._id !== taskId));  
      setNotification('Task deleted successfully.');
    } catch (error) {
      console.error('Error deleting task:', error);
      setNotification('Error deleting task.');
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'all') return true;
    return task.status === filter;
  });

  const searchedTasks = filteredTasks.filter(task =>
    task.title?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatDate = (date) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(date).toLocaleDateString(undefined, options);
  };

  return (
    <div className="App">
      <h1>Remote Teams</h1>

      {notification && <div className="notification">{notification}</div>}

      <form onSubmit={handleSubmit}>
        <h2>{taskToEdit ? 'Edit Task' : 'Add Task'}</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="date"
          placeholder="Due Date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />

        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
          required
        >
          <option value="">Select a User</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>
              {user.name}
            </option>
          ))}
        </select>

        <button type="submit">{taskToEdit ? 'Update Task' : 'Add Task'}</button>
      </form>

      <div className="task-filters">
        <input
          type="text"
          placeholder="Search by title"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>

        <select
          value={filterUser}
          onChange={(e) => setFilterUser(e.target.value)}
        >
          <option value="">All Users</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>
              {user.name}
            </option>
          ))}
        </select>
      </div>

      <h2>Tasks</h2>
      {searchedTasks.length > 0 ? (
        <ul className="task-list">
          {searchedTasks.map((task) => (
            <li key={task._id} className="task-item">
              <strong>{task.title}</strong> - {task.description} - {task.status} - Due: {task.dueDate ? formatDate(task.dueDate) : 'N/A'}
              <div className="task-actions">
                <button onClick={() => toggleTaskStatus(task)}>
                  Mark as {task.status === 'in-progress' ? 'Completed' : 'In Progress'}
                </button>
                <button onClick={() => handleEdit(task)}>Edit</button>
                <button onClick={() => handleDelete(task._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default App;
