import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Import CSS for styling

const userId = '66df023961c74851a7c06b5b'; // Static userId for now (for testing)

const App = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [tasks, setTasks] = useState([]);
  const [taskToEdit, setTaskToEdit] = useState(null);
  const [notification, setNotification] = useState('');
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [users, setUsers] = useState([]); // State to hold users
  const [selectedUser, setSelectedUser] = useState(''); // State to hold the selected user

  // Fetch tasks and users when the component loads
  useEffect(() => {
    fetchTasks();
    fetchUsers(); // Fetch users for the dropdown
  }, []);

  // Function to fetch tasks from the API
  const fetchTasks = async () => {
    try {
      const response = await axios.get(`http://localhost:9000/api/tasks?userId=${userId}`);
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setNotification('Error fetching tasks.');
    }
  };

  // Function to fetch users from the API
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:9000/api/users'); // Replace with your actual API
      setUsers(response.data); // Set the users in state
    } catch (error) {
      console.error('Error fetching users:', error);
      setNotification('Error fetching users.');
    }
  };

  // Function to handle task submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !dueDate || !selectedUser) {
      alert('Please fill out all fields');
      return;
    }

    try {
      if (taskToEdit) {
        // Update an existing task
        const response = await axios.put(`http://localhost:9000/api/tasks/${taskToEdit._id}`, {
          title,
          description,
          dueDate,
          userId: selectedUser, // Use the selected user
        });
        setTasks(tasks.map(task => task._id === response.data._id ? response.data : task));
        setTaskToEdit(null); // Reset edit state
        setNotification('Task updated successfully.');
      } else {
        // Add a new task
        const response = await axios.post('http://localhost:9000/api/tasks', {
          title,
          description,
          dueDate,
          userId: selectedUser, // Use the selected user for task assignment
        });
        setTasks([...tasks, response.data]);
        setNotification('Task added successfully.');
      }

      // Clear the form fields
      setTitle('');
      setDescription('');
      setDueDate('');
      setSelectedUser(''); // Clear selected user
    } catch (error) {
      console.error('Error adding/updating task:', error);
      setNotification('Error adding/updating task.');
    }
  };

  // Filter tasks by status
  const filteredTasks = tasks.filter(task => {
    if (filter === 'all') return true;
    return task.status === filter;
  });

  // Search tasks by title (use safe check to avoid toLowerCase() on undefined titles)
  const searchedTasks = filteredTasks.filter(task =>
    task.title?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Function to format date
  const formatDate = (date) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(date).toLocaleDateString(undefined, options);
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>

      {/* Notification */}
      {notification && <div className="notification">{notification}</div>}

      {/* Task Form */}
      <form onSubmit={handleSubmit}>
        <h2>{taskToEdit ? 'Edit Task' : 'Add Task'}</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="date"
          placeholder="Due Date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />

        {/* User dropdown to select who the task is assigned to */}
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
          required
        >
          <option value="">Select a User</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>
              {user.name}
            </option>
          ))}
        </select>

        <button type="submit">{taskToEdit ? 'Update Task' : 'Add Task'}</button>
      </form>

      {/* Task Filters */}
      <div className="task-filters">
        <input
          type="text"
          placeholder="Search by title"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select value={filter} onChange={(e) => setFilter(e.target.value)}>
          <option value="all">All</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
      </div>

      {/* Task List */}
      <h2>Tasks</h2>
      {searchedTasks.length > 0 ? (
        <ul className="task-list">
          {searchedTasks.map((task) => (
            <li key={task._id} className="task-item">
              <strong>{task.title}</strong> - {task.description} - {task.status} - Due: {task.dueDate ? formatDate(task.dueDate) : 'N/A'}
              <div className="task-actions">
                <button onClick={() => toggleTaskStatus(task)}>
                  Mark as {task.status === 'in-progress' ? 'Completed' : 'In Progress'}
                </button>
                <button onClick={() => handleEdit(task)}>Edit</button>
                <button onClick={() => handleDelete(task._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default App;
