import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskManager from './TaskManager'; // Import the TaskManager component

// Static userId
const userId = '66df023961c74851a7c06b5b';

const App = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [tasks, setTasks] = useState([]);

  // Fetch tasks when the component loads
  useEffect(() => {
    fetchTasks();
  }, []);

  // Function to fetch tasks from the API
  const fetchTasks = async () => {
    try {
      // Use backticks for template literal to embed the userId in the URL
      const response = await axios.get(`http://localhost:9000/api/users/${userId}/tasks`);
      setTasks(response.data); // Assuming response.data contains an array of tasks
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Function to handle task submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description) {
      alert('Please fill out both fields');
      return;
    }

    try {
      const response = await axios.post('http://localhost:9000/api/tasks', {
        title,
        description,
        userId, // Use the predefined userId variable
      });

      // Update the task list with the newly added task
      setTasks([...tasks, response.data.task]);
      setTitle('');  // Clear the title field
      setDescription('');  // Clear the description field
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <form onSubmit={handleSubmit}>
        <h2>Add Task</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">Add Task</button>
      </form>

      <h2>Tasks</h2>
      {tasks.length > 0 ? (
        <ul>
          {tasks.map((task) => (
            <li key={task._id}>
              <strong>{task.title}</strong> - {task.description}
            </li>
          ))}
        </ul>
      ) : (
        <p>No tasks available.</p>
      )}
    </div>
  );
};

export default App;
