import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

const API_URL = "http://localhost:9000/api"; // Ensure this matches your backend URL

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState(null);

  // Fetch tasks on component load
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(`${API_URL}/tasks`);
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const addTask = async () => {
    if (!title || !description) {
      alert("Please fill in both fields.");
      return;
    }

    try {
      const newTask = { title, description, userId: "your-user-id-here" }; // Replace with valid userId
      await axios.post(`${API_URL}/tasks`, newTask);
      fetchTasks(); // Refresh task list
      setTitle("");
      setDescription("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const editTask = async () => {
    if (!title || !description) {
      alert("Please fill in both fields.");
      return;
    }

    try {
      const updatedTask = { title, description };
      await axios.put(`${API_URL}/tasks/${currentTaskId}`, updatedTask);
      fetchTasks(); // Refresh task list
      setTitle("");
      setDescription("");
      setIsEditing(false);
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const deleteTask = async (id) => {
    if (window.confirm("Are you sure you want to delete this task?")) {
      try {
        await axios.delete(`${API_URL}/tasks/${id}`);
        fetchTasks(); // Refresh task list
      } catch (error) {
        console.error("Error deleting task:", error);
      }
    }
  };

  const handleEditClick = (task) => {
    setTitle(task.title);
    setDescription(task.description);
    setCurrentTaskId(task._id);
    setIsEditing(true);
  };

  return (
    <div className="App">
      <h1>Task Manager</h1>

      <div>
        <h2>{isEditing ? "Edit Task" : "Add Task"}</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        {isEditing ? (
          <button onClick={editTask}>Update Task</button>
        ) : (
          <button onClick={addTask}>Add Task</button>
        )}
      </div>

      <div>
        <h2>Tasks</h2>
        <ul>
          {tasks.map((task) => (
            <li key={task._id}>
              <h3>{task.title}</h3>
              <p>{task.description}</p>
              <button onClick={() => handleEditClick(task)}>Edit</button>
              <button onClick={() => deleteTask(task._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
